import React, {useState} from "react";
import "./Lista.css"
import Tarefa from "./Tarefa";

function Lista (props) {
   
    const [seletor, setSeletor] = useState("X");
    
    
    const pedidoDeDelecaoHandler = (idDaTarefa) => {
        props.deletarTarefa(idDaTarefa);

    }

    const pedidoDeFeitoHandler = (idDaTarefa) => {
        props.marcarFeito(idDaTarefa);
    }

    const filtroHandler = (e) => {
        if(e.target.value === "Todas"){
            setSeletor("X");
        }else if(e.target.value === "Feitas"){
            setSeletor(true);
        } else {
            setSeletor(false)
        }
        
        console.log(seletor)
    }

    

    return(
        <div className="row px-4">
            <form>
                <select name="estado">
                    <option selected={seletor} onChange={filtroHandler} value="Todas">Todas</option>
                    <option selected={seletor} onChange={filtroHandler} value="Feitas">Feitas</option>
                    <option selected={seletor} onChange={filtroHandler} value="N-feitas">NÃ£o Feitas</option>
                </select>
            </form>
            <div className="col-6 px-3 py-2 coluna">
                {props.tarefas.filter((e,i)=>!(i%2) && ((e.feito === seletor) || seletor === "X")).map((tarefa) => (
                    <Tarefa key={tarefa.id} pedidoDeFeitoHandler={pedidoDeFeitoHandler} pedidoDeDelecaoHandler = {pedidoDeDelecaoHandler} id = {tarefa.id} feito={tarefa.feito} titulo={tarefa.titulo} descricao={tarefa.descricao} prazo={tarefa.prazo} categoria={tarefa.categoria}/>
                ))}
            </div>
            <div className="col-6 px-3 py-2 coluna">
                {props.tarefas.filter((e,i)=>(i%2) && ((e.feito === seletor) || seletor === "X")).map((tarefa) => (
                    <Tarefa key={tarefa.id} pedidoDeFeitoHandler={pedidoDeFeitoHandler} pedidoDeDelecaoHandler = {pedidoDeDelecaoHandler} id = {tarefa.id} feito={tarefa.feito} titulo={tarefa.titulo} descricao={tarefa.descricao} prazo={tarefa.prazo} categoria={tarefa.categoria}/>
                ))}
            </div>
        </div>
    );
}

export default Lista;
