import React from "react";
import "./Lista.css"
import Tarefa from "./Tarefa";

function Lista (props) {
   
    const tarefasPares = [];
    const tarefasImpares = [];

    for(var i = 0; i < props.tarefas.length-1; i++){
        if(i%2 == 0){
            tarefasPares.push(props.tarefas[i]);
        }
        else{
            tarefasImpares.push(props.tarefas[i]);
        }
    }

    return(
        <div className="row px-4">
            <div className="col-6 px-3 py-2 coluna">
                {tarefasPares.map((tarefa) => (
                    <Tarefa titulo={tarefa.titulo} descricao={tarefa.descricao} prazo={tarefa.prazo} categoria={tarefa.categoria}/>
                ))}
            </div>
            <div className="col-6 px-3 py-2 coluna">
            {tarefasImpares.map((tarefa) => (
                    <Tarefa titulo={tarefa.titulo} descricao={tarefa.descricao} prazo={tarefa.prazo} categoria={tarefa.categoria}/>
                ))}
            </div>
        </div>
    );
}

export default Lista;
