    return (
        <div className="casca-tarefa my-2 py-2">
            <h4>{props.titulo}</h4>
            <p>{props.descricao}</p>
            <h5>{props.prazoDia} de {props.prazoMes} de {props.prazoAno}</h5>
            <button onClick={clickHandler} className="btn btn-primary">Feito!</button>
        </div>
    )
}

export default Tarefa;
