import React from "react";
import "./Lista.css"
import Tarefa from "./Tarefa";

function Lista () {
    const tarefasDados = [
        {
            prazoDia: 28,
            prazoMes: 'Janeiro',
            prazoAno: 2025,
            titulo: 'Limpar a Casa',
            descricao: 'Limpar a casa com vassoura e passar um pano.'
        },
        {
            prazoDia: 30,
            prazoMes: 'Janeiro',
            prazoAno: 2025,
            titulo: 'Lavar a Louça',
            descricao: 'Lavar os pratos da pia e limpar o ralo.'
        },
        {
            prazoDia: 31,
            prazoMes: 'Janeiro',
            prazoAno: 2025,
            titulo: 'Estudar React',
            descricao: 'Estudar Props e Estado.'
        }
    ];

    return(
        <div className="row px-4">
            <div className="col-6 px-3 py-2 coluna">
                <Tarefa 
                prazoDia = {tarefasDados[0].prazoDia}
                prazoMes = {tarefasDados[0].prazoMes}
                prazoAno = {tarefasDados[0].prazoAno}
                titulo = {tarefasDados[0].titulo}
                descricao = {tarefasDados[0].descricao}
                />
                <Tarefa prazoDia = {tarefasDados[2].prazoDia}
                prazoMes = {tarefasDados[2].prazoMes}
                prazoAno = {tarefasDados[2].prazoAno}
                titulo = {tarefasDados[2].titulo}
                descricao = {tarefasDados[2].descricao}/>
            </div>
            <div className="col-6 px-3 py-2 coluna">
                <Tarefa 
                prazoDia = {tarefasDados[1].prazoDia}
                prazoMes = {tarefasDados[1].prazoMes}
                prazoAno = {tarefasDados[1].prazoAno}
                titulo = {tarefasDados[1].titulo}
                descricao = {tarefasDados[1].descricao}
                />
            </div>
        </div>
    );
}

export default Lista;
