import React, {useState} from "react";
import "./Lista.css"
import Tarefa from "./Tarefa";

function Lista (props) {
   
    
    
    const pedidoDeDelecaoHandler = (idDaTarefa) => {
        props.deletarTarefa(idDaTarefa);

    }

    const pedidoDeFeitoHandler = (idDaTarefa) => {
        props.marcarFeito(idDaTarefa);
    }

    

    return(
        <div className="row px-4">
            
            <div className="col-6 px-3 py-2 coluna">
                {props.tarefas.filter((e,i)=>!(i%2)).map((tarefa) => (
                    <Tarefa key={tarefa.id} pedidoDeDelecaoHandler = {pedidoDeDelecaoHandler} id = {tarefa.id} feito={tarefa.feito} titulo={tarefa.titulo} descricao={tarefa.descricao} prazo={tarefa.prazo} categoria={tarefa.categoria}/>
                ))}
            </div>
            <div className="col-6 px-3 py-2 coluna">
                {props.tarefas.filter((e,i)=>i%2).map((tarefa) => (
                    <Tarefa key={tarefa.id} pedidoDeDelecaoHandler = {pedidoDeDelecaoHandler} id = {tarefa.id} titulo={tarefa.titulo} descricao={tarefa.descricao} prazo={tarefa.prazo} categoria={tarefa.categoria}/>
                ))}
            </div>
        </div>
    );
}

export default Lista;
