import React from "react";
import "./Tarefa.css";

function Tarefa () {
    const prazo = new Date(2025, 0, 28);
    const nome = 'Limpar a casa';
    const descricao = 'Limpar a casa com vassoura e passar um pano.';


    return (
        <div className="casca-tarefa my-2 py-2">
            <h4>{nome}</h4>
            <p>{descricao}</p>
            <h5>{prazo.getDay()} de {prazo.getMonth()} de {prazo.getFullYear()}</h5>
            <button className="btn btn-primary">Feito!</button>
        </div>
    )
}

export default Tarefa;
